import { Route, useLocation, BrowserRouter, Routes } from "react-router-dom";
import "./App.css";
import { useEffect, useState } from "react";
import { accessToken, logout, getCurrentUserProfile } from "./spotify";
import { catchErrors } from "./utils";

function App() {
  const [token, setToken] = useState(null);
  const [profile, setProfile] = useState(null);
  useEffect(() => {
    setToken(accessToken);

    const fetchData = async () => {
      if (accessToken) {
        const { data } = await getCurrentUserProfile();
        setProfile(data);
      }
    };

    catchErrors(fetchData());
  }, []);

  /*const topArtists = () => {
    return (<h1>Top Artists</h1>)
  }
  const topTracks = () => {
    return (<h1>Top Tracks</h1>)
  }
  const topArtists = () => {
    return (<h1>Top Artists</h1>)
  }
  const playlistID = () => {
    return (<h1>Playlist</h1>)
  }
  const playlists = () => {
    return (<h1>Playlists/h1>)
  }*/
  return (
    /*<div className="App">
      <header className="App-header">
        {!token ? (
          <a className="App-link" href="http://localhost:8080/login">
            Log in to Spotify
          </a>
        ) : (

        )}
      </header>
    </div>*/
  <BrowserRouter>
    <Routes>
      <Route path="/top-artists">
        <>
          <h1>Top Artists</h1>
        </>
      </Route>
      <Route path="/top-tracks">
        <>
          <h1>Top Tracks</h1>
        </>
      </Route>
      <Route path="/playlists/:id">
        <>
          <h1>Playlist</h1>
        </>
      </Route>
      <Route path="/playlists">
        <>
          <h1>Playlists</h1>
        </>
      </Route>
      <Route path="/">
        <>
          <button onClick={logout}>Log Out</button>

          {profile && (
              <div>
                <h1>{profile.display_name}</h1>
                <p>{profile.followers.total} Followers</p>
              </div>
          )}
        </>
      </Route>
    </Routes>
  </BrowserRouter>
  );
}

export default App;
