  }
  const topArtists = () => {
    return (<h1>Top Artists</h1>)
  }
  const playlistID = () => {
    return (<h1>Playlist</h1>)
  }
  const playlists = () => {
    return (<h1>Playlists/h1>)
  }*/
 /* return (
    <div className="App">
      <header className="App-header">
        {!token ? (
          <a className="App-link" href="http://localhost:8080/login">
            Log in to Spotify
          </a>
        ) : (
          <BrowserRouter>
            <Routes>
              <Route path="/top-artists">
                <>
                  <h1>Top Artists</h1>
                </>
              </Route>
              <Route path="/top-tracks">
                <>
                  <h1>Top Tracks</h1>
                </>
              </Route>
              <Route path="/playlists/:id">
                <>
                  <h1>Playlist</h1>
                </>
              </Route>
              <Route path="/playlists">
                <>
                  <h1>Playlists</h1>
                </>
              </Route>
              <Route path="/">
                <>
                  <button onClick={logout}>Log Out</button>

                  {profile && (
                    <div>
                      <h1>{profile.display_name}</h1>
                      <p>{profile.followers.total} Followers</p>
                    </div>
                  )}
                </>
              </Route>
            </Routes>
          </BrowserRouter>
        )}
      </header>
    </div>
  );*/
  return (
    <BrowserRouter>
      <Routes>
        <Route
          path="/top-artists"
          element={
            <>
              <h1>Top Artists</h1>
            </>
          }
        />
        <Route
          path="/top-tracks"
          element={
            <>
              <h1>Top Tracks</h1>
            </>
          }
        />
        <Route
          path="/playlists/:id"
          element={
            <>
              <h1>Playlist</h1>
            </>
          }
        />
        <Route
          path="/playlists"
          element={
            <>
              <h1>Playlists</h1>
            </>
          }
        />
        <Route
          path="/"
          element={
            <>
              <button onClick={logout}>Log Out</button>

              {profile && (
                <div>
                  <h1>{profile.display_name}</h1>
                  <p>{profile.followers.total} Followers</p>
                </div>
              )}
            </>
          }
        />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
